x = [ 7, 7, 4, 5, 9, 9,          4, 12, 8, 1, 8, 7,          3, 13, 2, 1, 17, 7,          12, 5, 6, 2, 1, 13,          14, 10, 2, 4, 9, 11,          3, 5, 12, 6, 10, 7]          sigma = 5;n = columns(x) * rows(x);mean_x = mean(mean(x));alpha = 0.05;left = mean_x - (sigma / sqrt(n)) * norminv(1 - alpha/2, 0, 1);right =  mean_x - (sigma / sqrt(n)) * norminv(alpha/2, 0, 1);fprintf("confidence interval = (%.3f, %.3f)\n", left, right);%b)s = std(std(x));df = n - 1;tValue = tinv(alpha/2, df);leftBound = mean_x + tValue * (s/sqrt(n));rightBound = mean_x - tValue * (s/sqrt(n));fprintf("confidence interval = (%.3f, %.3f)\n", leftBound, rightBound);